function o(){if("IntersectionObserver"in window){const a=new IntersectionObserver(e=>{e.forEach(t=>{t.isIntersecting?setTimeout(()=>{t.target.classList.add("animate"),t.target.classList.remove("no-opacity")},200):setTimeout(()=>{t.target.classList.remove("animate"),t.target.classList.add("no-opacity")},200)})});let s=document.getElementsByClassName("animate");console.log(s);for(let e=0;e<s.length;e++)s[e].classList.add("no-opacity"),a.observe(s[e])}else console.log("Este es el problema")}export{o as a};
